//import logo from './logo.svg';
import './App.css';
import { v4 as uuidv4 } from 'uuid';
import { useState } from 'react';
import { NumericFormat } from 'react-number-format';
import { format } from "date-fns";
import Cookies from 'universal-cookie';
import { MapContainer, TileLayer, useMap } from 'react-leaflet';

const cookies = new Cookies();

let style = { width: '66%' };
function App() {
  let order = []
  let searchTerm = ""
  const [completed, setCompleted] = useState(0);
  const [produce, setProduce] = useState([]);
  const [loading, setLoading] = useState(false);
  const [words, setWords] = useState([]);
  const [selectedWord, setSelected] = useState(null)
  const upload = (event) => {
    const file = event.target.files[0];
    if (!file) {
      setCompleted(0);
      return;
    }
    setLoading(true);
    const id = uuidv4();
    let formData = new FormData();
    formData.append('file', file);
    formData.append('id', id)
    fetch('http://localhost:2000/upload/', { method: 'POST', body: formData }).then(async (d) => {
      const data = await d.json();
      setLoading(false);
    });
  }
  const verify = (id) => {
    fetch(`http://localhost:4000/verify/${id}/`, { method: 'GET' }).then(async (d) => {
      const data = await d.json();
      if (data.state != 1) {
        setTimeout(() => { verify(id) }, 1000)
      }
      if (data.state) {
        setCompleted(Math.round(data.state * 100));
        if (data.state == 1) {
          document.getElementsByTagName('input')[0].value = null;
          setCompleted(0);
          browse();
        }
      }

    });
  }
  const browse = () => {
    order = (cookies.get('order_by') || '').split(',');
    searchTerm = (cookies.get('search_term') || '');
    setProduce([])
    setLoading(true)
    fetch(`http://localhost:4000/browse/?order=${order.join(',')}&term=${searchTerm}`, { method: 'GET' }).then(async (d) => {
      setLoading(false)
      const data = await d.json();
      setProduce(data["result"])
    });
  }
  const orderBy = (field) => {
    order = (cookies.get('order_by') || '').split(',');
    console.log("order by")
    if (!order.length)
      order.push(field)
    else {
      if ([field, `-${field}`].indexOf(order[order.length - 1]) == -1) {
        order.push(field);
      } else {
        if (order[order.length - 1] == field) order[order.length - 1] = `-${field}`
        else {
          if (order[order.length - 1] == `-${field}`) order[order.length - 1] = field

        }
      }
    }
    while (order.length > 2) order.shift()
    cookies.set('order_by', order.join(','), { path: '/' });
    browse()
  }
  const search = (event) => {
    browse()
  }
  const setTerm = (event) => {
    searchTerm = event.target.value
    cookies.set('search_term', searchTerm, { path: '/' });
  }
  const keyUp = (event) => {
    if (event.keyCode == 13) browse()
  }
  const searchRoad = (event) => {
    switch (event.keyCode) {
      case 38:
        if (!selectedWord) setSelected(0)
        if (selectedWord > 0) setSelected(selectedWord - 1)
        break;
      case 40:
        if (selectedWord == null) setSelected(0)
        else
          if (selectedWord < words.length - 1) setSelected(selectedWord + 1)
        break
      case 13:
        if (selectedWord != null) {
          if (words[selectedWord])
            event.target.value = words[selectedWord]
        }
        setWords([])
        break
      default:
        fetch(`http://localhost:2000/search/?t=${event.target.value}`, { method: 'GET' }).then(async (d) => {
          setLoading(false)
          setSelected(null)
          const data = await d.json();
          console.log(data)
          setWords(data.words)
        });
    }
  }

  let percents = completed;
  let products = produce
  return (
    <div>
      <div className="App p-8">
        <label className="block mb-2 text-sm font-medium text-gray-900 dark:text-white" htmlFor="file_input">Upload files</label>
        <input
          onChange={upload}
          className="block w-full text-sm text-gray-900 border border-gray-300 rounded-lg cursor-pointer bg-gray-50 dark:text-gray-400 focus:outline-none dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400" id="file_input" type="file" />
      </div>
      <div className="p-8 w-full rounded-full h-2.5 dark:bg-gray-700">
        <div className="bg-blue-600 h-2.5 rounded-full" style={{ width: percents + '%' }}></div>
      </div>

      <div className="max-w-md mx-auto">

        <div id="hs-combobox-json-example-based-on-api-pathes" className="relative" data-hs-combo-box='{
  "apiUrl": "http://localhost:3001/search/",
  "apiSearchPath": "name",
  "apiSearchDefaultPath": "all",
  "outputItemTemplate": "<div class=\"cursor-pointer py-2 px-4 w-full text-sm text-gray-800 hover:bg-gray-100 rounded-lg focus:outline-hidden focus:bg-gray-100 dark:bg-neutral-900 dark:hover:bg-neutral-800 dark:text-neutral-200 dark:focus:bg-neutral-800\" data-hs-combo-box-output-item><div class=\"flex justify-between items-center w-full\"><div><div class=\"hidden\" data-hs-combo-box-output-item-field=\"ccn3\"></div><div data-hs-combo-box-output-item-field=\"name.common\" data-hs-combo-box-search-text data-hs-combo-box-value></div></div><span class=\"hidden hs-combo-box-selected:block\"><svg class=\"shrink-0 size-3.5 text-blue-600 dark:text-blue-500\" xmlns=\"http:.w3.org/2000/svg\" width=\"24\" height=\"24\" viewBox=\"0 0 24 24\" fill=\"none\" stroke=\"currentColor\" strokeWidth=\"2\" strokeLinecap=\"round\" strokeLinejoin=\"round\"><polyline points=\"20 6 9 17 4 12\"></polyline></svg></span></div></div>"
}'>
          <div className="relative">
            <input className="py-2.5 sm:py-3 ps-4 pe-9 block w-full border-gray-200 rounded-lg sm:text-sm focus:border-blue-500 focus:ring-blue-500 disabled:opacity-50 disabled:pointer-events-none dark:bg-neutral-900 dark:border-neutral-700 dark:text-neutral-400 dark:placeholder-neutral-500 dark:focus:ring-neutral-600" type="text" role="combobox" aria-expanded="false" data-hs-combo-box-input="" onKeyUp={searchRoad} />
            <div className="absolute top-1/2 end-3 -translate-y-1/2" aria-expanded="false" role="button" data-hs-combo-box-toggle="">
              <svg className="shrink-0 size-3.5 text-gray-500 dark:text-neutral-500" xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="m7 15 5 5 5-5"></path>
                <path d="m7 9 5-5 5 5"></path>
              </svg>
            </div>
          </div>
          <div id="listbox" className="absolute z-50 w-full max-h-72 p-1 bg-white border border-gray-200 rounded-lg overflow-hidden overflow-y-auto [&::-webkit-scrollbar]:w-2 [&::-webkit-scrollbar-thumb]:rounded-full [&::-webkit-scrollbar-track]:bg-gray-100 [&::-webkit-scrollbar-thumb]:bg-gray-300 dark:[&::-webkit-scrollbar-track]:bg-neutral-700 dark:[&::-webkit-scrollbar-thumb]:bg-neutral-500 dark:bg-neutral-900 dark:border-neutral-700" style={{ display: (words.length) ? 'block' : 'none' }} role="listbox" data-hs-combo-box-output="">
            {words.map((item, index) => (
              <div className={`flex justify-between items-center p-2 w-full ${(selectedWord == index) ? "bg-blue-800 text-white" : "bg-white"}`} key={`words_${item}`} >{item}</div>
            ))}
          </div>
        </div>
      </div>


      <div className="max-w-md mx-auto">
        <label htmlFor="default-search" className="mb-2 text-sm font-medium text-gray-900 sr-only dark:text-white">Search</label>
        <div className="relative">
          <div className="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
            <svg className="w-4 h-4 text-gray-500 dark:text-gray-400" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 20 20">
              <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="m19 19-4-4m0-7A7 7 0 1 1 1 8a7 7 0 0 1 14 0Z" />
            </svg>
          </div>
          <input onKeyUp={keyUp} onChange={setTerm} type="search" id="default-search" className="block w-full p-4 ps-10 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Filter..." required />
          <button onClick={search} type="submit" className="text-white absolute end-2.5 bottom-2.5 bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Search</button>
        </div>
      </div>

      <div className="p-8 w-full rounded-full h-2.5 dark:bg-gray-700">


        <div className="relative overflow-x-auto">
          <table className="w-full text-sm text-left rtl:text-right text-gray-500 dark:text-gray-400">
            <thead className="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
              <tr>
                <th scope="col" className=" px-6 py-3">
                  <a onClick={() => { orderBy('name') }} href="#">Product name</a>
                </th>
                <th scope="col" className="px-6 py-3 ">
                  <a onClick={() => { orderBy('expiration') }} href="#">Expires</a>
                </th>
                <th scope="col" className="px-6 py-3 ">
                  <a onClick={() => { orderBy('price') }} href="#">Price</a>
                </th>
                <th scope="col" className="px-6 py-3 ">
                  <a onClick={() => { orderBy('price') }} href="#">BRL</a>
                </th>
                <th scope="col" className="px-6 py-3 ">
                  <a onClick={() => { orderBy('price') }} href="#">EUR</a>
                </th>
                <th scope="col" className="px-6 py-3 ">
                  <a onClick={() => { orderBy('price') }} href="#">TND</a>
                </th>
                <th scope="col" className="px-6 py-3 ">
                  <a onClick={() => { orderBy('price') }} href="#">CYP</a>
                </th>
                <th scope="col" className="px-6 py-3 ">
                  <a onClick={() => { orderBy('price') }} href="#">NOK</a>
                </th>

              </tr>
            </thead>
            <tbody>
              {products.map((item, index) => (

                <tr key={`product${index}`} className="bg-white border-b dark:bg-gray-800 dark:border-gray-700 border-gray-200">
                  <th scope="row" className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white max-w-96">
                    {item['name']}
                  </th>
                  <td className="px-6 py-4">
                    {format(item.expiration, "M/d/yyyy")}

                  </td>
                  <td className="px-6 py-4">
                    <NumericFormat value={item.price} allowLeadingZeros thousandSeparator="," index="index" prefix={'$'} decimalScale={2} />

                  </td>
                  <td className="px-6 py-4">
                    <NumericFormat value={item.brl} allowLeadingZeros thousandSeparator="," index="index" prefix={'R$'} decimalScale={2} />

                  </td>
                  <td className="px-6 py-4">
                    <NumericFormat value={item.eur} allowLeadingZeros thousandSeparator="," index="index" prefix={'€'} decimalScale={2} />

                  </td>
                  <td className="px-6 py-4">
                    <NumericFormat value={item.tnd} allowLeadingZeros thousandSeparator="," index="index" prefix={'	د.ت'} decimalScale={2} />

                  </td>
                  <td className="px-6 py-4">
                    <NumericFormat value={item.cyp} allowLeadingZeros thousandSeparator="," index="index" prefix={'CYP'} decimalScale={2} />

                  </td>
                  <td className="px-6 py-4">
                    <NumericFormat value={item.nok} allowLeadingZeros thousandSeparator="," index="index" prefix={'NOK'} decimalScale={2} />

                  </td>
                </tr>
              ))}

            </tbody>
          </table>
        </div>

      </div>
      {loading &&
        <div className="text-center m-4">
          <div role="status">
            <svg aria-hidden="true" className="inline w-8 h-8 text-gray-200 animate-spin dark:text-gray-600 fill-blue-600" viewBox="0 0 100 101" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M100 50.5908C100 78.2051 77.6142 100.591 50 100.591C22.3858 100.591 0 78.2051 0 50.5908C0 22.9766 22.3858 0.59082 50 0.59082C77.6142 0.59082 100 22.9766 100 50.5908ZM9.08144 50.5908C9.08144 73.1895 27.4013 91.5094 50 91.5094C72.5987 91.5094 90.9186 73.1895 90.9186 50.5908C90.9186 27.9921 72.5987 9.67226 50 9.67226C27.4013 9.67226 9.08144 27.9921 9.08144 50.5908Z" fill="currentColor" />
              <path d="M93.9676 39.0409C96.393 38.4038 97.8624 35.9116 97.0079 33.5539C95.2932 28.8227 92.871 24.3692 89.8167 20.348C85.8452 15.1192 80.8826 10.7238 75.2124 7.41289C69.5422 4.10194 63.2754 1.94025 56.7698 1.05124C51.7666 0.367541 46.6976 0.446843 41.7345 1.27873C39.2613 1.69328 37.813 4.19778 38.4501 6.62326C39.0873 9.04874 41.5694 10.4717 44.0505 10.1071C47.8511 9.54855 51.7191 9.52689 55.5402 10.0491C60.8642 10.7766 65.9928 12.5457 70.6331 15.2552C75.2735 17.9648 79.3347 21.5619 82.5849 25.841C84.9175 28.9121 86.7997 32.2913 88.1811 35.8758C89.083 38.2158 91.5421 39.6781 93.9676 39.0409Z" fill="currentFill" />
            </svg>
            <span className="sr-only">Loading...</span>
          </div>
        </div>
      }

    </div>


  );
}

export default App;
